ObjC.import('stdlib');
app = Application.currentApplication();
app.includeStandardAdditions = true;

// Emoji-Regex: https://stackoverflow.com/questions/37089427/javascript-find-emoji-in-string-and-parse
const emojiRegex = "(?:[\u2700-\u27bf]|(?:\ud83c[\udde6-\uddff]){2}|[\ud800-\udbff][\udc00-\udfff]|[\u0023-\u0039]\ufe0f?\u20e3|\u3299|\u3297|\u303d|\u3030|\u24c2|\ud83c[\udd70-\udd71]|\ud83c[\udd7e-\udd7f]|\ud83c\udd8e|\ud83c[\udd91-\udd9a]|\ud83c[\udde6-\uddff]|[\ud83c[\ude01-\ude02]|\ud83c\ude1a|\ud83c\ude2f|[\ud83c[\ude32-\ude3a]|[\ud83c[\ude50-\ude51]|\u203c|\u2049|[\u25aa-\u25ab]|\u25b6|\u25c0|[\u25fb-\u25fe]|\u00a9|\u00ae|\u2122|\u2139|\ud83c\udc04|[\u2600-\u26FF]|\u2b05|\u2b06|\u2b07|\u2b1b|\u2b1c|\u2b50|\u2b55|\u231a|\u231b|\u2328|\u23cf|[\u23e9-\u23f3]|[\u23f8-\u23fa]|\ud83c\udccf|\u2934|\u2935|[\u2190-\u21ff])";
/*------------*/

var vault_path = $.getenv('vault_path');
let homepath = app.pathTo('home folder');
vault_path = vault_path.replace(/^~/, homepath);
const vaultPathLength = vault_path.length + 1;
const vault_name = vault_path.replace (/.*\/(.*)$/,"$1");

let files = app.doShellScript("echo | find '" + vault_path + "' -not -path '*/.obsidian/*' -not -path '*/.trash/*' -not -path '*.DS_Store*' -not -path '*Icon?'");

var file_array = files.split ("\r");

let jsonArray = [];
file_array.forEach(file => {
  // filename extraction
  let name = file.replace (/.*\/(.*?)$/,"$1");
  let type = file.replace (/.*\/.*?(\.\w+)?$/,"$1");
  let relativePath = file.substring(vaultPathLength, (file.length - name.length - 1));
  let absolutePath = file;

  //icon
  let icon = "";
  if (type == ".md"){
    icon ="📄 ";
  } else if (type == ".png"){
    icon ="🏞 ";
  } else if (type == ".pdf"){
    icon ="📜 ";
  } else if (type == ".csv"){
    icon ="📊 ";
  } else if (type == ""){
    icon ="📁 ";
  }

  //no icon, if emoji at beginning already
  var emojiFirstRegEx = new RegExp ("^" + emojiRegex)
  if (name.match(emojiFirstRegEx)) {
    icon = "";
  }

//new Note
    jsonArray.push ({
        'title': '❇️ New note with title: ',
        'arg': 'NEW;;',
	    'uid': 'newobsidian'
    })

  //push result
  if (type != ""){
      jsonArray.push ({
        'title': icon + name,
        'subtitle': vault_name + " ▹ " + relativePath,
        'arg': absolutePath,
		'type':'file',
		'uid': absolutePath
      })
  } else {


  }

});

JSON.stringify({ 'items': jsonArray });

